}" -- javascript function RegisterJavaScriptById("ID_RECT", "BlickFunction", script) ID_RECT.BlickFunction() end // JavaScript function _onmousedown() { var script = "function BlickFunction()
{PrintMessage("call...")
}" // javascript function RegisterJavaScriptById("ID_RECT", "BlickFunction", script); ID_RECT.BlickFunction(); }

